import React, { FC, useCallback, useContext, useEffect, useState, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { ProductContext } from 'context';
import { CartItem, Product } from 'interfaces';

export const Products: FC = () => {
  const navigate = useNavigate();

  const handleGoToCart = useCallback(() => {
    navigate('/cart');
  }, [navigate]);

  const { products, addToCart, removeFromCart, fetchProducts } = useContext(ProductContext);
  const [sortState, setSortState] = useState({ key: '', direction: '' });
  const [catFlag, setSortFlag] = useState<boolean>(false);
  const [idFlag, setIdFlag] = useState<boolean>(false);

  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  const sortedItems = useMemo(() => {
    let sortableProducts = [...products];
    if (sortState !== null) {
      sortableProducts.sort((a, b) => {
        if (a.price < b.price) {
          return sortState.direction === 'lowest' ? -1 : 1;
        }
        if (a.price > b.price) {
          return sortState.direction === 'lowest' ? 1 : -1;
        }
        return 0;
      });
    }
    return sortableProducts;
  }, [sortState, products]);

  const [_, setResortedArray] = useState<Product[]>(sortedItems);

  const handleSortByPrice = useCallback(() => {
    let direction = 'lowest';
    if (sortState && sortState.key === direction && sortState.direction === 'lowest') {
      direction = 'highest';
    }
    setSortState({ ...sortState, direction });
  }, [sortState, setSortState]);

  const handleSortByCategory = useCallback(() => {
    setSortFlag(!catFlag);
    const newArr = sortedItems.sort((a, b): number => {
      return catFlag ? a.category.id.localeCompare(b.category.id) : b.category.id.localeCompare(a.category.id);
    });
    setResortedArray(newArr);
  }, [sortedItems, catFlag]);

  const handleRemoveFromCart = useCallback(
    (id: number) => {
      removeFromCart(id);
    },
    [removeFromCart],
  );

  const handleAddToCart = useCallback(
    (item: CartItem) => {
      addToCart(item);
    },
    [addToCart],
  );

  const handleSortByID = useCallback(() => {
    setIdFlag(!idFlag);
    setResortedArray(sortedItems.sort((a, b) => (idFlag ? a.id - b.id : b.id - a.id)));
  }, [idFlag, sortedItems]);

  return (
    <div style={{ margin: '50px' }}>
      <button onClick={handleGoToCart} className="goto">
        Go To Cart
      </button>

      <table>
        <tbody>
          <tr>
            <th>
              <button onClick={handleSortByID}>ID</button>
            </th>
            <th>NAME</th>
            <th>
              <button onClick={handleSortByCategory}>CATEGORY</button>
            </th>
            <th>
              <button
                onClick={() => {
                  handleSortByPrice();
                }}
              >
                PRICE
              </button>
            </th>
            <th>ACTIONS</th>
          </tr>
          {sortedItems.map((cartItems) => {
            return (
              <tr key={cartItems.id}>
                <td>{cartItems.id}</td>
                <td> {cartItems.name}</td>
                <td> {cartItems.category.name}</td>
                <td>${cartItems.price}</td>
                <td>
                  <button onClick={() => handleRemoveFromCart(cartItems.id)}>REMOVE</button> Select{' '}
                  <button onClick={() => handleAddToCart({ ...cartItems, quantity: 1 })}>ADD</button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};
