import { ProductContext } from 'context';
import React, { FC, useContext, useEffect } from 'react';
import { useState } from 'react';
import { useMemo } from 'react';

export const Products: FC = () => {
  const { products, addToCart, removeFromCart, cartItems, fetchProducts } = useContext(ProductContext);
  const [sortState, setSortState] = useState({ key: 0, direction: '' });

  useEffect(() => {
    fetchProducts();
    console.log()
  }, [fetchProducts]);

  const sortedItems = useMemo(() => {
    let sortableProducts = [...cartItems];

    if (sortState !== null) {
      sortableProducts.sort((a, b) => {
        //@ts-expect-error
        if (a[sortState.key] < b[sortState.key]) {
          return sortState.direction === 'ascending' ? -1 : 1;
        }
        //@ts-expect-error
        if (a[sortState.key] > b[sortState.key]) {
          return sortState.direction === 'ascending' ? 1 : -1;
        }
        return 0;
      });
    }
    return sortableProducts;
  }, [sortState, products]);

  const handleSort = (key: any) => {
    let direction = 'ascending';
    if (sortState && sortState.key === key && sortState.direction === 'ascending') {
      direction = 'descending';
    }
    setSortState({ key, direction });
  };

  return (
    <div style={{ margin: '50px' }}>
      <table>
        <tbody>
          <tr>
            <th>ID</th>
            <th>NAME</th>
            <th>
              <button /*onClick={() => handleSort("category")}*/>CATEGORY</button>
            </th>
            <th>
              <button onClick={() => handleSort('price')}>PRICE</button>
            </th>
            <th>ACTIONS</th>
          </tr>
          {sortedItems.map((product) => {
            return (
              <tr key={product.id}>
                <td>{product.id}</td>
                <td> {product.name}</td>
                <td> {product.category.name}</td>
                <td>${product.price}</td>
                <td>
                  <button onClick={(e) => removeFromCart(product.id)}>-</button> Select{' '}
                  <button onClick={(e) => addToCart(product)}>+</button>
                </td>
              </tr>
            );
          })}
        </tbody>
        {"leght"}
      </table>
    </div>
  );
};
