import React, { FC, useCallback, useContext } from 'react';
import { CartItem } from 'interfaces';
import { useNavigate } from 'react-router-dom';
import { ProductContext } from 'context';

export const Cart: FC = () => {
  const { cartItems, addToCart, removeFromCart } = useContext(ProductContext);

  const handleRemoveProduct = useCallback(
    (id) => {
      removeFromCart(id);
    },
    [removeFromCart],
  );
  const navigate = useNavigate()

  const handleGoToProducts = useCallback(()=>{},[])
  console.log(cartItems);
  return (
    <div style={{ margin: '50px' }}>

      <button onClick={handleGoToProducts}> GO To Products</button>


      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>NAME</th>
            <th>CATEGORY</th>
            <th>PRICE</th>
            <th>QUANTITY</th>
            <th>ACTIONS</th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((product: CartItem) => {
            return (
              <tr key={product.id}>
                <td>{product.id}</td>
                <td> {product.name}</td>
                <td> {product.category.name}</td>
                <td>{product.price}</td>
                <td>{product.quantity}</td>
                <td>
                  <button onClick={() => handleRemoveProduct(product.id)}>Remove product</button> Select
                  <button onClick={(e) => addToCart(product)}>Add Product</button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};
