import { ProductContext } from 'context';
import { Product } from 'interfaces';
import React, { FC, useCallback, useContext, useEffect } from 'react';
import { useState } from 'react';
import { useMemo } from 'react';

export const Products: FC = () => {
  const { products, addToCart, removeFromCart, cartItems, fetchProducts } = useContext(ProductContext);
  const [sortState, setSortState] = useState({ key: '', direction: '' });
  const [sortFlag, setSortFlag] = useState<boolean>(false);

  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  const sortedItems = useMemo(() => {
    let sortableProducts = [...products];
    if (sortState !== null) {
      sortableProducts.sort((a, b) => {
        if (a.price < b.price) {
          return sortState.direction === 'lowest' ? -1 : 1;
        }
        if (a.price > b.price) {
          return sortState.direction === 'lowest' ? 1 : -1;
        }
        return 0;
      });
    }
    return sortableProducts;
  }, [sortState, products]);

  const [_, setResortedArray] = useState<Product[]>(sortedItems);

  const handleSortByPrice = useCallback(
    (key: string) => {
      let direction = 'lowest';
      if (sortState && sortState.key === key && sortState.direction === 'lowest') {
        direction = 'highest';
      }
      setSortState({ key, direction });
    },
    [sortState, setSortState],
  );

  const handleSortByCategory = useCallback(() => {
    setSortFlag(sortFlag?false:true);
    console.log(sortFlag);
    const newArr = sortedItems.sort((a, b): number => {
      return sortFlag ? a.category.id.localeCompare(b.category.id) : b.category.id.localeCompare(a.category.id);
    });
    setResortedArray(newArr);
  }, [sortedItems]);

  return (
    <div style={{ margin: '50px' }}>
      <table>
        <tbody>
          <tr>
            <th>ID</th>
            <th>NAME</th>
            <th>
              <button onClick={handleSortByCategory}>CATEGORY</button>
            </th>
            <th>
              <button
                onClick={() => {
                  handleSortByPrice('highest');
                }}
              >
                PRICE
              </button>
            </th>
            <th>ACTIONS</th>
          </tr>
          {sortedItems.map((cartItems) => {
            return (
              <tr key={cartItems.id}>
                <td>{cartItems.id}</td>
                <td> {cartItems.name}</td>
                <td> {cartItems.category.name}</td>
                <td>${cartItems.price}</td>
                <td>
                  <button onClick={(e) => removeFromCart(cartItems.id)}>-</button> Select{' '}
                  <button onClick={(e) => addToCart({ ...cartItems, quantity: 1 })}>+</button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};
